MQL:
db.station.aggregate([
  {
    $match: {
      city: { $ne: "Palo Alto" }
    }
  },
  {
    $unwind: "$status"
  },
  {
    $group: {
      _id: null,
      avg_bikes_available: { $avg: "$status.bikes_available" }
    }
  },
  {
    $project: {
      _id: 0,
      avg_bikes_available: 1
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.PetType": {
        $in: ["cat", "dog"]
      }
    }
  },
  {
    $project: {
      Fname: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $match: { city: "San Francisco" }
  },
  {
    $unwind: "$status"
  },
  {
    $group: {
      _id: "$id",
      avg_bikes_available: { $avg: "$status.bikes_available" }
    }
  },
  {
    $match: { avg_bikes_available: { $gt: 10 } }
  },
  {
    $project: {
      id: "$_id",
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.PetType": "cat",
      "Docs1.pet_age": 3
    }
  },
  {
    $project: {
      LName: 1,
      _id: 0
    }
  }
]);

db.trip.aggregate([
  {
    $lookup: {
      from: "station",
      localField: "end_station_id",
      foreignField: "id",
      as: "Docs1"
    }
  },
  {
    $project: {
      id: 1,
      installation_date: {
        $arrayElemAt: ["$Docs1.installation_date", 0]
      },
      _id: 0
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $unwind: "$countries.car_makers"
  },
  {
    $unwind: "$countries.car_makers.model_list"
  },
  {
    $unwind: "$countries.car_makers.model_list.car_names"
  },
  {
    $unwind: "$countries.car_makers.model_list.car_names.cars_data"
  },
  {
    $match: {
      "countries.car_makers.model_list.car_names.cars_data.Cylinders": 3
    }
  },
  {
    $project: {
      Horsepower: "$countries.car_makers.model_list.car_names.cars_data.Horsepower",
      Make: "$countries.car_makers.model_list.car_names.Make"
    }
  },
  {
    $sort: {
      Horsepower: -1
    }
  },
  {
    $limit: 1
  },
  {
    $project: {
      Horsepower: 1,
      Make: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $match: {
      city: "Palo Alto"
    }
  },
  {
    $lookup: {
      from: "trips",
      localField: "id",
      foreignField: "end_station_id",
      as: "Docs1"
    }
  },
  {
    $group: {
      _id: "$name",
      sum_Docs1: { $sum: { $size: "$Docs1" } }
    }
  },
  {
    $match: {
      sum_Docs1: { $lte: 100 }
    }
  },
  {
    $project: {
      name: "$_id",
      _id: 0
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $unwind: "$countries.car_makers"
  },
  {
    $unwind: "$countries.car_makers.model_list"
  },
  {
    $group: {
      _id: {
        Id: "$countries.car_makers.Id",
        FullName: "$countries.car_makers.FullName"
      },
      modelCount: { $sum: 1 }
    }
  },
  {
    $project: {
      Id: "$_id.Id",
      FullName: "$_id.FullName",
      count: "$modelCount",
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $unwind: {
      path: "$Docs1",
      preserveNullAndEmptyArrays: true
    }
  },
  {
    $match: {
      $or: [
        { "Docs1.PetType": { $ne: "cat" } },
        { "Docs1": { $exists: false } }
      ]
    }
  },
  {
    $group: {
      _id: "$StuID"
    }
  },
  {
    $project: {
      StuID: "$_id",
      _id: 0
    }
  }
]);

db.trip.aggregate([
  {
    $sort: {
      duration: 1
    }
  },
  {
    $limit: 1
  },
  {
    $lookup: {
      from: "station",
      localField: "start_station_id",
      foreignField: "id",
      as: "Docs1"
    }
  },
  {
    $project: {
      lat: {
        $arrayElemAt: ["$Docs1.lat", 0]
      },
      long: {
        $arrayElemAt: ["$Docs1.long", 0]
      },
      city: {
        $arrayElemAt: ["$Docs1.city", 0]
      },
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $project: {
      _id: 0,
      name: 1,
      has_7_bikes_available: {
        $in: [7, "$status.bikes_available"]
      }
    }
  },
  {
    $match: {
      has_7_bikes_available: true
    }
  },
  {
    $group: {
      _id: "$name"
    }
  },
  {
    $project: {
      _id: 0,
      name: "$_id"
    }
  }
]);

db.trip.aggregate([
  {
    $group: {
      _id: {
        start_station_id: "$start_station_id",
        start_station_name: "$start_station_name"
      },
      count: { $sum: 1 }
    }
  },
  {
    $match: {
      count: { $gte: 200 }
    }
  },
  {
    $project: {
      start_station_id: "$_id.start_station_id",
      start_station_name: "$_id.start_station_name",
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.PetType": { $ne: "cat" }
    }
  },
  {
    $project: {
