db.Pets.aggregate([
  {
    $group: {
      _id: "$PetType",
      avg_pet_age: { $avg: "$pet_age" },
      max_pet_age: { $max: "$pet_age" }
    }
  },
  {
    $project: {
      _id: 0,
      avg_pet_age: 1,
      max_pet_age: 1,
      PetType: "$_id"
    }
  }
]);

db.Student.aggregate([
  {
    $project: {
      avg_age: {
        $cond: [
          {
            $eq: [
              {
                $size: "$Has_Pet"
              },
              0
            ]
          },
          "$Age",
          null
        ]
      }
    }
  },
  {
    $group: {
      _id: null,
      avg_age: {
        $avg: "$avg_age"
      }
    }
  },
  {
    $project: {
      _id: 0,
      avg_age: 1
    }
  }
]);

db.continents.aggregate([
