    }
  },
  {
    $match: {
      avg_bikes_available: { $gt: 10 }
    }
  },
  {
    $project: {
      _id: 0,
      name: 1
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $unwind: "$countries.car_makers"
  },
  {
    $project: {
      FullName: "$countries.car_makers.FullName",
      Id: "$countries.car_makers.Id",
      count_model_list: {
        $size: "$countries.car_makers.model_list"
      }
    }
  },
  {
    $match: {
      count_model_list: {
        $gt: 3
      }
    }
  },
  {
    $project: {
      FullName: 1,
      Id: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $match: {
      lat: { $gt: 37.4 }
    }
  },
  {
    $project: {
      id: 1,
      has_low_bikes: {
        $gte: [
          { $size: { $filter: { input: "$status", as: "status", cond: { $lt: ["$$status.bikes_available", 7] } } } },
          1
        ]
      }
    }
  },
  {
    $match: {
      has_low_bikes: false
    }
  },
  {
    $project: {
      id: 1,
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $addFields: {
      hasDog: {
        $in: ["dog", "$Docs1.PetType"]
      },
      hasCat: {
        $in: ["cat", "$Docs1.PetType"]
      }
    }
  },
  {
    $match: {
      hasDog: true,
      hasCat: false
    }
  },
  {
    $project: {
      Fname: 1,
      Age: 1,
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $project: {
      Fname: 1,
      Sex: 1,
      count_Docs1: {
        $size: "$Docs1"
      }
    }
  },
  {
    $match: {
      count_Docs1: {
        $gt: 1
      }
    }
  },
  {
    $project: {
      Fname: 1,
      Sex: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $project: {
      name: 1,
      id: 1,
      bikes_available_avg: {
        $avg: "$status.bikes_available"
      },
      installation_month: {
        $month: {
          $dateFromString: {
            dateString: "$installation_date"
          }
        }
      }
    }
  },
  {
    $match: {
      $or: [
        { bikes_available_avg: { $gt: 14 } },
        { installation_month: 12 }
      ]
    }
  },
  {
    $project: {
      name: 1,
      id: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  {
    $match: {
      city: "San Jose"
    }
  },
  {
    $group: {
      _id: null,
      avg_lat: { $avg: "$lat" },
      avg_long: { $avg: "$long" }
    }
  },
  {
    $project: {
      _id: 0,
      avg_lat: 1,
      avg_long: 1
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $project: {
      CountryName: "$countries.CountryName",
      hasCarMakers: {
        $cond: { if: { $isArray: "$countries.car_makers" }, then: { $gt: [{ $size: "$countries.car_makers" }, 0] }, else: false }
      }
    }
  },
  {
    $match: {
      hasCarMakers: false
    }
  },
  {
    $project: {
      CountryName: 1,
      _id: 0
    }
  }
]);

db.trip.aggregate([
  {
    $group: {
      _id: "$start_station_id",
      avg_trip_duration: { $avg: "$duration" }
    }
  },
  {
    $lookup: {
      from: "station",
      localField: "_id",
      foreignField: "id",
      as: "Docs1"
    }
  },
  {
    $project: {
      name: { $arrayElemAt: ["$Docs1.name", 0] },
      long: { $arrayElemAt: ["$Docs1.long", 0] },
      avg_trip_duration: 1,
      _id: 0
    }
  }
]);

db.continents.aggregate([
  {
    $project: {
      countries: {
        $filter: {
          input: "$countries",
          as: "country",
          cond: {
            $or: [
              { $gt: [{ $size: "$$country.car_makers" }, 3] },
              {
                $gt: [
                  {
                    $size: {
                      $filter: {
                        input: "$$country.car_makers",
                        as: "maker",
                        cond: {
                          $gt: [
                            {
                              $size: {
                                $filter: {
                                  input: "$$maker.model_list",
                                  as: "model",
                                  cond: {
                                    $eq: ["$$model.Model", "fiat"]
                                  }
                                }
                              }
                            },
                            0
                          ]
                        }
                      }
                    }
                  },
                  0
                ]
              }
            ]
          }
        }
      }
    }
  },
  {
    $unwind: "$countries"
  },
  {
    $project: {
   _id:0,
   CountryId: "$countries.CountryId",
      CountryName: "$countries.CountryName"
    }
  }
]);

db.trip.aggregate([
  {
    $group: {
      _id: "$end_station_id",
      min_trip_duration: { $min: "$duration" }
    }
  },
  {
    $lookup: {
      from: "station",
      localField: "_id",
      foreignField: "id",
      as: "Docs1"
    }
  },
  {
    $project: {
      name: { $arrayElemAt: ["$Docs1.name", 0] },
      lat: { $arrayElemAt: ["$Docs1.lat", 0] },
      min_trip_duration: 1,
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $match: {
      Sex: "F",
      "Docs1.PetType": "dog"
    }
  },
  {
    $group: {
      _id: null,
      count: {
        $sum: 1
      }
    }
  },
  {
    $project: {
      _id: 0,
      count: 1
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $project: {
      CountryName: "$countries.CountryName",
      CountryId: "$countries.CountryId",
      count_car_maker: {
        $size: "$countries.car_makers"
      }
    }
  },
  {
    $match: {
      count_car_maker: {
        $gt: 0
      }
    }
  },
  {
    $project: {
      CountryName: 1,
