MQL:
db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $lookup: {
      from: "departments",
      localField: "employees.EMPLOYEE_ID",
      foreignField: "employees.EMPLOYEE_ID",
      as: "Docs1"
    }
  },
  {
    $project: {
      employee: {
        $cond: [
          {
            $eq: [{ $size: "$employees.job_history" }, 0]
          },
          "$employees",
          {
            $cond: [
              {
                $eq: [{ $size: "$Docs1.employees.job_history" }, 0]
              },
              "$Docs1.employees",
              false
            ]
          }
        ]
      }
    }
  },
  {
    $match: {
      employee: { $ne: false }
    }
  },
  {
    $project: {
      EMPLOYEE_ID: "$employee.EMPLOYEE_ID",
      FIRST_NAME: "$employee.FIRST_NAME",
      LAST_NAME: "$employee.LAST_NAME",
      EMAIL: "$employee.EMAIL",
      PHONE_NUMBER: "$employee.PHONE_NUMBER",
      HIRE_DATE: "$employee.HIRE_DATE",
      JOB_ID: "$employee.JOB_ID",
      SALARY: "$employee.SALARY",
      COMMISSION_PCT: "$employee.COMMISSION_PCT",
      MANAGER_ID: "$employee.MANAGER_ID",
      DEPARTMENT_ID: "$employee.DEPARTMENT_ID",
      _id: 0
    }
  }
]);

db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      "employees.FIRST_NAME": {
        $not: {
          $regex: "M",
          $options: "i"
        }
      }
    }
  },
  {
    $project: {
      FIRST_NAME: "$employees.FIRST_NAME",
      LAST_NAME: "$employees.LAST_NAME",
      HIRE_DATE: "$employees.HIRE_DATE",
      SALARY: "$employees.SALARY",
      DEPARTMENT_ID: "$employees.DEPARTMENT_ID",
      _id: 0
    }
  }
]);

db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      $or: [
        { "employees.FIRST_NAME": { $regex: "D", $options: "i" } },
        { "employees.FIRST_NAME": { $regex: "S", $options: "i" } }
      ]
    }
  },
  {
    $sort: { "employees.SALARY": -1 }
  },
  {
    $project: {
      _id: 0,
      EMPLOYEE_ID: "$employees.EMPLOYEE_ID",
      FIRST_NAME: "$employees.FIRST_NAME",
      LAST_NAME: "$employees.LAST_NAME",
      EMAIL: "$employees.EMAIL",
      PHONE_NUMBER: "$employees.PHONE_NUMBER",
      HIRE_DATE: "$employees.HIRE_DATE",
      JOB_ID: "$employees.JOB_ID",
      SALARY: "$employees.SALARY",
      COMMISSION_PCT: "$employees.COMMISSION_PCT",
      MANAGER_ID: "$employees.MANAGER_ID",
      DEPARTMENT_ID: "$employees.DEPARTMENT_ID"
    }
  }
]);

db.Projects.aggregate([
  {
    $project: {
      Project_ID: 1,
      Project_Details: 1,
      count_Documents: {
        $size: "$Documents"
      }
    }
  },
  {
    $match: {
      count_Documents: {
        $gte: 2
      }
    }
  },
  {
    $project: {
      Project_ID: 1,
      Project_Details: 1,
      _id: 0
    }
  }
]);

db.airports.aggregate([
  {
    $match: {
      city: "New York"
    }
  },
  {
    $project: {
      name: 1,
      city: 1,
      country: 1,
      elevation: 1,
      _id: 0
    }
  }
]);

db.departments.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $lookup: {
      from: "regions",
      let: { location_id: "$LOCATION_ID" },
      pipeline: [
        { $unwind: "$countries" },
        { $unwind: "$countries.locations" },
        {
          $match: {
            $expr: {
              $eq: ["$countries.locations.LOCATION_ID", "$$location_id"]
            }
          }
        },
        {
          $project: {
            COUNTRY_NAME: "$countries.COUNTRY_NAME"
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      FIRST_NAME: "$employees.FIRST_NAME",
      LAST_NAME: "$employees.LAST_NAME",
      EMPLOYEE_ID: "$employees.EMPLOYEE_ID",
      COUNTRY_NAME: "$Docs1.COUNTRY_NAME",
      _id: 0
    }
  }
]);

db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      "employees.SALARY": { $gt: 0, $lte: 2500 }
