]);

db.Students.aggregate([
  {
    $project: {
      student_id: 1,
      personal_name: 1,
      count_enrollment: {
        $size: "$Student_Course_Enrolment"
      }
    }
  },
  {
    $match: {
      count_enrollment: {
        $gte: 2
      }
    }
  },
  {
    $project: {
      student_id: 1,
      personal_name: 1,
      _id: 0
    }
  }
]);

db.Projects.aggregate([
  {
    $unwind: "$Documents"
  },
  {
    $unwind: "$Documents.Statements"
  },
  {
    $group: {
      _id: {
        statement_id: "$Documents.Statements.Statement_ID",
        statement_details: "$Documents.Statements.Statement_Details"
      },
      count_accounts: {
        $sum: {
          $size: "$Documents.Statements.Accounts"
        }
      }
    }
  },
  {
    $sort: {
      count_accounts: -1
    }
  },
  {
    $limit: 1
  },
  {
    $project: {
      Statement_ID: "$_id.statement_id",
      Statement_Details: "$_id.statement_details",
      _id: 0
    }
  }
]);

db.company.aggregate([
  {
    $lookup: {
      from: "gas_station",
      let: { companyId: "$Company_ID" },
      pipeline: [
        { $unwind: "$station_company" },
        { $match: { $expr: { $eq: ["$station_company.Company_ID", "$$companyId"] } } }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $group: {
      _id: "$Company",
      count: { $sum: 1 }
    }
  },
  {
    $project: {
      Company: "$_id",
      count: 1,
      _id: 0
    }
  }
]);

db.Products.aggregate([
