  },
  {
    $unwind: "$wine"
  },
  {
    $match: {
      "wine.Year": { $lt: 2005 }
    }
  },
  {
    $group: {
      _id: null,
      max_Price: { $max: "$wine.Price" }
    }
  },
  {
    $project: {
      _id: 0,
      max_Price: 1
    }
  }
]);

db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      "employees.EMPLOYEE_ID": 163
    }
  },
  {
    $project: {
      _id: 0,
      salary: "$employees.SALARY"
    }
  },
  {
    $lookup: {
      from: "jobs",
      let: { salary: "$salary" },
      pipeline: [
        { $unwind: "$employees" },
        {
          $match: {
            $expr: {
              $gt: ["$employees.SALARY", "$$salary"]
            }
          }
        },
        {
          $project: {
            _id: 0,
            FIRST_NAME: "$employees.FIRST_NAME",
            LAST_NAME: "$employees.LAST_NAME"
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      FIRST_NAME: "$Docs1.FIRST_NAME",
      LAST_NAME: "$Docs1.LAST_NAME"
    }
  }
]);

db.grapes.aggregate([
  {
    $unwind: "$wine"
  },
  {
    $group: {
      _id: {
        grape: "$Grape",
        color: "$Color"
      },
      avg_price: {
        $avg: "$wine.Price"
      }
    }
  },
  {
    $sort: {
      avg_price: -1
    }
  },
  {
    $limit: 1
  },
  {
    $project: {
      _id: 0,
      Color: "$_id.color"
    }
  }
]);

db.Customers.aggregate([
