    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      last_name: "$Docs1.last_name",
      _id: 0
    }
  }
]);

db.cinema.aggregate([
  {
    $match: {
      "Openning_year": { $gte: 2011 }
    }
  },
  {
    $group: {
      _id: null,
      avg_capacity: { $avg: "$Capacity" },
      min_capacity: { $min: "$Capacity" },
      max_capacity: { $max: "$Capacity" }
    }
  },
  {
    $project: {
      _id: 0,
