      min_capacity: 1,
      max_capacity: 1
    }
  }
]);

db.jobs.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      "employees.HIRE_DATE": { $lt: "2002-06-21" }
    }
  },
  {
    $project: {
      _id: 0,
      EMPLOYEE_ID: "$employees.EMPLOYEE_ID",
      FIRST_NAME: "$employees.FIRST_NAME",
      LAST_NAME: "$employees.LAST_NAME",
      EMAIL: "$employees.EMAIL",
      PHONE_NUMBER: "$employees.PHONE_NUMBER",
      HIRE_DATE: "$employees.HIRE_DATE",
      JOB_ID: "$employees.JOB_ID",
      SALARY: "$employees.SALARY",
      COMMISSION_PCT: "$employees.COMMISSION_PCT",
      MANAGER_ID: "$employees.MANAGER_ID",
      DEPARTMENT_ID: "$employees.DEPARTMENT_ID"
    }
  }
]);

db.Ship.aggregate([
  {
    $unwind: "$captain"
  },
  {
    $match: {
      $or: [
        { "captain.Class": "Cutter" },
        { "captain.Class": "Armed schooner" }
      ]
    }
  },
  {
    $group: {
      _id: "$captain.Rank",
      classes: {
        $addToSet: "$captain.Class"
      }
    }
  },
  {
    $match: {
      classes: {
        $all: ["Cutter", "Armed schooner"]
      }
    }
  },
  {
    $project: {
      Rank: "$_id",
      _id: 0
    }
  }
]);

db.company.aggregate([
  {
    $group: {
      _id: null,
      min_market_value: { $min: "$Market_Value" },
      max_market_value: { $max: "$Market_Value" },
      avg_market_value: { $avg: "$Market_Value" }
    }
  },
  {
    $project: {
      _id: 0,
      min_market_value: 1,
      max_market_value: 1,
      avg_market_value: 1
    }
  }
]);

db.company.aggregate([
  {
    $lookup: {
      from: "gas_station",
