    }
  },
  {
    $match: {
      Docs1: { $size: 0 }
    }
  },
  {
    $project: {
      Company: 1,
      Main_Industry: 1,
      _id: 0
    }
  }
]);

db.actor.aggregate([
  {
    $lookup: {
      from: "musical",
      localField: "Musical_ID",
      foreignField: "Musical_ID",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $group: {
      _id: {
        Musical_ID: "$Docs1.Musical_ID",
        Name: "$Docs1.Name"
      },
      COUNT: {
        $sum: 1
      }
    }
  },
  {
    $project: {
      Name: "$_id.Name",
      COUNT: 1,
      _id: 0
    }
  }
]);

db.company.aggregate([
  {
    $group: {
      _id: "$Main_Industry",
      total_market_value: { $sum: "$Market_Value" },
      count: { $sum: 1 }
