  },
  {
    $sort: { total_market_value: -1 }
  },
  {
    $limit: 1
  },
  {
    $project: {
      Main_Industry: "$_id",
      count: 1,
      _id: 0
    }
  }
]);

db.employee.aggregate([
  {
    $project: {
      name: 1,
      salary: 1,
      count_certificate: { $size: "$certificate" }
    }
  },
  {
    $sort: { count_certificate: -1 }
  },
  {
    $limit: 1
  },
  {
    $project: {
      name: 1,
      salary: 1,
      _id: 0
    }
  }
]);

db.shop.aggregate([
  {
    $match: {
      $or: [
        { Open_Year: { $gt: 2012 } },
        { Open_Year: { $lt: 2008 } }
      ]
    }
  },
  {
    $group: {
      _id: "$Location",
      count: { $sum: 1 }
    }
  },
  {
    $match: {
      count: { $gt: 1 }
    }
  },
  {
    $project: {
      Location: "$_id",
      _id: 0
    }
  }
]);

db.airports.aggregate([
  {
    $match: {
      country: "United States"
    }
  },
  {
    $group: {
      _id: "$city",
      airportCount: {
        $sum: 1
      }
    }
  },
  {
    $match: {
      airportCount: {
        $gt: 3
      }
    }
  },
  {
    $project: {
      city: "$_id",
      _id: 0
    }
  }
]);

db.Department.aggregate([
  {
    $unwind: "$Course"
  },
  {
    $match: {
      "Course.CName": "INTRODUCTION TO COMPUTER SCIENCE"
    }
  },
  {
    $project: {
      DName: 1,
      Room: 1,
      _id: 0
    }
  }
]);

db.Subjects.aggregate([
  {
    $unwind: "$Courses"
  },
  {
    $project: {
      course_description: "$Courses.course_description",
      course_name: "$Courses.course_name",
      enrollment_count: {
        $size: {
          $filter: {
            input: "$Courses.Student_Course_Enrolment",
            as: "enrollment",
            cond: {
              $ne: ["$$enrollment.student_id", null]
            }
          }
        }
      }
    }
  },
  {
    $match: {
      enrollment_count: {
        $gt: 2
      }
    }
  },
  {
    $project: {
      course_description: 1,
      course_name: 1,
      _id: 0
    }
  }
]);

db.shop.aggregate([
  {
    $unwind: "$stock"
  },
  {
    $group: {
      _id: "$Shop_ID",
      sum_Quantity: { $sum: "$stock.Quantity" }
    }
  },
  {
    $sort: { sum_Quantity: -1 }
  },
  {
    $limit: 1
  },
  {
    $lookup: {
      from: "shop",
      localField: "_id",
      foreignField: "Shop_ID",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      Shop_Name: "$Docs1.Shop_Name",
      _id: 0
    }
  }
]);

db.Students.aggregate([
  {
    $project: {
      count: {
        $cond: {
          if: {
            $eq: [{ $size: "$Student_Course_Enrolment" }, 0]
          },
          then: 1,
          else: 0
        }
      }
    }
  },
  {
    $group: {
      _id: null,
      count: {
        $sum: "$count"
      }
    }
  },
  {
    $project: {
      _id: 0,
      count: 1
    }
  }
]);

db.device.aggregate([
  {
    $lookup: {
      from: "shop",
      localField: "Device_ID",
      foreignField: "stock.Device_ID",
      as: "Docs1"
    }
  },
  {
    $match: {
      Docs1: { $size: 0 }
    }
  },
  {
    $project: {
      Carrier: 1,
      _id: 0
    }
  }
]);

db.driver.aggregate([
  {
    $unwind: "$school_bus"
  },
  {
    $group: {
      _id: null,
      max_years_working: { $max: "$school_bus.Years_Working" },
      min_years_working: { $min: "$school_bus.Years_Working" },
      avg_years_working: { $avg: "$school_bus.Years_Working" }
    }
  },
  {
    $project: {
      _id: 0,
      max_years_working: 1,
      min_years_working: 1,
      avg_years_working: 1
    }
  }
])

db.Ship.aggregate([
  {
    $unwind: "$captain"
  },
  {
    $group: {
      _id: "$Name",
      midshipman_count: {
        $sum: {
          $cond: [
            { $eq: ["$captain.Rank", "Midshipman"] },
            1,
            0
          ]
        }
      },
      lieutenant_count: {
        $sum: {
          $cond: [
            { $eq: ["$captain.Rank", "Lieutenant"] },
            1,
            0
          ]
        }
      }
    }
  },
  {
    $match: {
      midshipman_count: { $gt: 0 },
      lieutenant_count: { $gt: 0 }
    }
  },
  {
    $project: {
      Name: "$_id",
      _id: 0
    }
  }
]);

