  {
    $group: {
      _id: "$Denomination",
      foundedBefore1890: {
        $addToSet: {
          $cond: [{ $lt: ["$Founded", 1890] }, "$Founded", null]
        }
      },
      foundedAfter1900: {
        $addToSet: {
          $cond: [{ $gt: ["$Founded", 1900] }, "$Founded", null]
        }
      }
    }
  },
  {
    $project: {
      _id: 0,
      Denomination: "$_id",
      foundedBefore1890: {
        $filter: {
          input: "$foundedBefore1890",
          as: "year",
          cond: { $ne: ["$$year", null] }
        }
      },
      foundedAfter1900: {
        $filter: {
          input: "$foundedAfter1900",
          as: "year",
          cond: { $ne: ["$$year", null] }
        }
      }
    }
  },
  {
    $match: {
      $and: [
        { "foundedBefore1890.0": { $exists: true } },
        { "foundedAfter1900.0": { $exists: true } }
      ]
    }
  },
  {
    $project: {
      Denomination: 1
    }
  }
]);

