  },
  {
    $project: {
      Fname: "$Docs1.Fname",
      Lname: "$Docs1.Lname",
      _id: 0
    }
  }
]);

db.departments.aggregate([
  {
    $unwind: "$employees"
  },
  {
    $match: {
      "employees.HIRE_DATE": { $gt: "1987-09-07" }
    }
  },
  {
    $project: {
      _id: 0,
      EMPLOYEE_ID: "$employees.EMPLOYEE_ID",
      FIRST_NAME: "$employees.FIRST_NAME",
      LAST_NAME: "$employees.LAST_NAME",
      EMAIL: "$employees.EMAIL",
      PHONE_NUMBER: "$employees.PHONE_NUMBER",
      HIRE_DATE: "$employees.HIRE_DATE",
      JOB_ID: "$employees.JOB_ID",
      SALARY: "$employees.SALARY",
      COMMISSION_PCT: "$employees.COMMISSION_PCT",
      MANAGER_ID: "$employees.MANAGER_ID",
      DEPARTMENT_ID: "$employees.DEPARTMENT_ID"
    }
  }
]);

db.editor.aggregate([
  {
    $lookup: {
      from: "journal",
      localField: "journal_committee.Journal_ID",
      foreignField: "Journal_ID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.Sales": { $gt: 3000 }
    }
  },
  {
    $project: {
      Name: 1,
      _id: 0
    }
  }
]);

db.Projects.aggregate([
  {
    $unwind: "$Documents"
  },
  {
    $unwind: "$Documents.Documents_with_Expenses"
  },
  {
    $lookup: {
      from: "Ref_Budget_Codes",
      localField: "Documents.Documents_with_Expenses.Budget_Type_Code",
      foreignField: "Budget_Type_Code",
      as: "Docs1"
    }
  },
  {
    $match: {
      $or: [
        {
          "Docs1.Budget_Type_Description": "Government"
        },
        {
          "Documents.Documents_with_Expenses.Document_Details": "Government"
        }
      ]
    }
  },
  {
    $project: {
      Document_ID: "$Documents.Document_ID",
      _id: 0
    }
  }
]);
