MQL:
db.list.aggregate([
  {
    $group: {
      _id: "$Grade",
      count_DISTINCT_classroom: { $addToSet: "$Classroom" },
      count: { $sum: 1 }
    }
  },
  {
    $project: {
      Grade: "$_id",
      count_DISTINCT_classroom: { $size: "$count_DISTINCT_classroom" },
      count: 1,
      _id: 0
    }
  }
]);

db.Ref_Service_Types.aggregate([
  {
    $unwind: "$Services"
  },
  {
    $group: {
      _id: {
        Service_Type_Description: "$Service_Type_Description",
        Service_Type_Code: "$Service_Type_Code"
      },
      count: {
        $sum: {
          $size: "$Services.Bookings_Services"
        }
      }
    }
  },
  {
    $sort: {
      count: -1
    }
  },
  {
    $limit: 1
  },
  {
    $project: {
      Service_Type_Description: "$_id.Service_Type_Description",
      Service_Type_Code: "$_id.Service_Type_Code",
      _id: 0
    }
  }
]);

db.Club.aggregate([
  {
    $unwind: "$Member_of_club"
  },
  {
    $lookup: {
      from: "Student",
      localField: "Member_of_club.StuID",
      foreignField: "StuID",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $match: {
      "Docs1.Advisor": 1121
    }
  },
  {
    $group: {
      _id: "$ClubID",
      ClubName: { $first: "$ClubName" }
    }
  },
  {
    $project: {
      _id: 0,
      ClubName: 1
    }
  }
]);

db.ACCOUNTS.aggregate([
  {
    $unwind: "$CHECKING"
  },
  {
    $sort: {
      "CHECKING.balance": -1
    }
  },
  {
    $limit: 1
  },
  {
    $lookup: {
      from: "ACCOUNTS",
      localField: "custid",
      foreignField: "custid",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $unwind: "$Docs1.SAVINGS"
  },
  {
    $project: {
      balance: "$Docs1.SAVINGS.balance",
      _id: 0
    }
  }
]);

db.College.aggregate([
  {
    $unwind: "$Tryout"
  },
  {
    $group: {
      _id: "$state",
      midPlayers: {
        $addToSet: {
          $cond: [{ $eq: ["$Tryout.pPos", "mid"] }, "$Tryout.pID", null]
        }
      },
      goaliePlayers: {
        $addToSet: {
          $cond: [{ $eq: ["$Tryout.pPos", "goalie"] }, "$Tryout.pID", null]
        }
      }
    }
  },
  {
    $project: {
      state: "$_id",
      hasMidPlayers: {
        $gt: [{ $size: { $filter: { input: "$midPlayers", as: "player", cond: { $ne: ["$$player", null] } } } }, 0]
      },
      hasGoaliePlayers: {
        $gt: [{ $size: { $filter: { input: "$goaliePlayers", as: "player", cond: { $ne: ["$$player", null] } } } }, 0]
      }
    }
  },
  {
    $match: {
      hasMidPlayers: true,
      hasGoaliePlayers: false
    }
  },
  {
    $project: {
      state: 1,
      _id: 0
    }
  }
]);

db.Video_Games.aggregate([
  {
    $lookup: {
      from: "Student",
      localField: "GameID",
      foreignField: "Plays_Games.GameID",
      as: "Docs1"
    }
  },
  {
    $match: {
      Docs1: { $ne: [] }
    }
  },
  {
    $count: "count"
  }
]);

db.Ref_Attraction_Types.aggregate([
  {
    $unwind: "$Tourist_Attractions"
  },
  {
    $unwind: "$Tourist_Attractions.Visits"
  },
  {
    $group: {
      _id: {
        Tourist_Attraction_ID: "$Tourist_Attractions.Tourist_Attraction_ID",
        Name: "$Tourist_Attractions.Name"
      },
      visitCount: {
        $sum: 1
      }
    }
  },
  {
    $match: {
      visitCount: { $lte: 1 }
    }
  },
  {
    $project: {
      _id: 0,
      Tourist_Attraction_ID: "$_id.Tourist_Attraction_ID",
      Name: "$_id.Name"
    }
  }
]);

db.Addresses.aggregate([
  {
    $match: {
      state_province_county: "Colorado"
    }
  },
  {
    $group: {
      _id: "$town_city",
      count: {
        $sum: 1
      }
    }
  },
  {
    $group: {
      _id: null,
      count: {
        $sum: 1
      }
    }
  },
  {
    $project: {
      _id: 0,
      count: 1
    }
  }
]);

db.Artist.aggregate([
  {
    $unwind: "$Album"
  },
  {
    $unwind: "$Album.Track"
  },
  {
    $lookup: {
      from: "Genre",
      localField: "Album.Track.GenreId",
      foreignField: "GenreId",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $match: {
      "Docs1.Name": { $in: ["Reggae", "Rock"] }
    }
  },
  {
    $group: {
      _id: "$Album.AlbumId",
      Title: { $first: "$Album.Title" },
      Genres: { $addToSet: "$Docs1.Name" }
    }
  },
  {
    $match: {
      Genres: { $all: ["Reggae", "Rock"] }
    }
  },
  {
    $project: {
      _id: 0,
      Title: 1
    }
  }
]);

db.medicine.aggregate([
  {
    $unwind: "$medicine_enzyme_interaction"
  },
  {
    $group: {
      _id: "$id",
      name: { $first: "$name" },
      count: { $sum: 1 }
    }
  },
  {
    $match: {
      count: { $gte: 2 }
    }
  },
  {
    $project: {
      id: "$_id",
      name: 1,
      _id: 0
    }
  }
]);

db.singer.aggregate([
  {
    $group: {
      _id: null,
      avg_Age: { $avg: "$Age" }
    }
  },
  {
    $lookup: {
      from: "singer",
      let: { avg_Age: "$avg_Age" },
      pipeline: [
        {
          $match: {
            $expr: { $gt: ["$Age", "$$avg_Age"] }
          }
        },
        {
          $project: {
            Song_Name: 1,
            _id: 0
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $replaceRoot: { newRoot: "$Docs1" }
  },
  {
    $project: {
      Song_Name: 1,
      _id: 0
    }
  }
]);

db.department.aggregate([
  {
