db.pitStops.aggregate([
  {
    $lookup: {
      from: "pitStops",
      let: { raceId: "$raceId", duration: "$duration" },
      pipeline: [
        {
          $match: {
            $expr: {
              $and: [
                { $eq: ["$raceId", 841] },
                { $lt: ["$$duration", "$duration"] }
              ]
            }
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $match: {
      Docs1: { $ne: [] }
    }
  },
  {
    $project: {
      driverId: 1,
      stop: 1,
      _id: 0
    }
  }
]);

db.Physician.aggregate([
