db.artist.aggregate([
  {
    $unwind: "$volume"
  },
  {
    $group: {
      _id: "$Artist_ID",
      Famous_Title: { $first: "$Famous_Title" },
      more_than_2_weeks: {
        $max: {
          $cond: [{ $gt: ["$volume.Weeks_on_Top", 2] }, 1, 0]
        }
      },
      less_than_2_weeks: {
        $max: {
          $cond: [{ $lt: ["$volume.Weeks_on_Top", 2] }, 1, 0]
        }
      }
    }
  },
  {
    $match: {
      more_than_2_weeks: 1,
      less_than_2_weeks: 1
    }
  },
  {
    $project: {
      Famous_Title: 1,
      _id: 0
    }
  }
]);

db.Product.aggregate([
  {
    $lookup: {
      from: "Staff",
      localField: "Problems.reported_by_staff_id",
      foreignField: "staff_id",
      as: "Docs1"
    }
  },
  {
    $addFields: {
      laceyBosco: {
        $filter: {
          input: "$Docs1",
          as: "staff",
          cond: {
            $and: [
              { $eq: ["$$staff.staff_first_name", "Lacey"] },
              { $eq: ["$$staff.staff_last_name", "Bosco"] }
            ]
          }
        }
      },
      kentonChamplin: {
        $filter: {
          input: "$Docs1",
          as: "staff",
          cond: {
            $and: [
              { $eq: ["$$staff.staff_first_name", "Kenton"] },
              { $eq: ["$$staff.staff_last_name", "Champlin"] }
            ]
          }
        }
      }
    }
  },
  {
    $match: {
      $and: [
        { "laceyBosco.0": { $exists: true } },
        { "kentonChamplin.0": { $exists: true } }
      ]
    }
  },
  {
    $project: {
      product_name: 1,
      _id: 0
    }
  }
]);

db.drivers.aggregate([
  {
    $unwind: {
      path: "$pitStops",
      preserveNullAndEmptyArrays: true
    }
  },
  {
    $unwind: {
      path: "$results",
      preserveNullAndEmptyArrays: true
    }
  },
  {
    $group: {
      _id: "$driverId",
      forename: { $first: "$forename" },
      surname: { $first: "$surname" },
      pitStopCount: { $sum: { $cond: [{ $ifNull: ["$pitStops", false] }, 1, 0] } },
      raceResultCount: { $sum: { $cond: [{ $ifNull: ["$results", false] }, 1, 0] } }
    }
  },
  {
    $match: {
      $or: [
        { pitStopCount: { $gt: 8 } },
        { raceResultCount: { $gt: 5 } }
      ]
    }
  },
  {
    $project: {
      driverId: "$_id",
      forename: 1,
      surname: 1,
      _id: 0
    }
  }
]);

