db.department.aggregate([
  {
    $unwind: "$instructor"
  },
  {
    $match: {
      "instructor.dept_name": "Biology"
    }
  },
  {
    $group: {
      _id: null,
      biologySalaries: { $addToSet: "$instructor.salary" }
    }
  },
  {
    $lookup: {
      from: "department",
      pipeline: [
        { $unwind: "$instructor" },
        {
          $match: {
            "instructor.salary": { $exists: true }
          }
        },
        {
          $project: {
            name: "$instructor.name",
            salary: "$instructor.salary"
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      name: "$Docs1.name",
      salary: "$Docs1.salary",
      biologySalaries: 1
    }
  },
  {
    $match: {
      $expr: {
        $gt: [
          "$salary",
          { $min: "$biologySalaries" }
        ]
      }
    }
  },
  {
    $project: {
      name: 1,
      _id: 0
    }
  }
]);

