db.artist.aggregate([
  {
    $unwind: "$exhibition"
  },
  {
    $project: {
      Theme: "$exhibition.Theme",
      attendance_below_100: {
        $filter: {
          input: "$exhibition.exhibition_record",
          as: "record",
          cond: {
            $lt: ["$$record.Attendance", 100]
          }
        }
      },
      attendance_above_500: {
        $filter: {
          input: "$exhibition.exhibition_record",
          as: "record",
          cond: {
            $gt: ["$$record.Attendance", 500]
          }
        }
      }
    }
  },
  {
    $match: {
      $expr: {
        $and: [
          { $ne: [{ $size: "$attendance_below_100" }, 0] },
          { $ne: [{ $size: "$attendance_above_500" }, 0] }
        ]
      }
    }
  },
  {
    $project: {
      Theme: 1,
      _id: 0
    }
  }
]);

