]);

db.genre.aggregate([

  { $unwind: { path: "$song", preserveNullAndEmptyArrays: true } },

  { $unwind: { path: "$artist", preserveNullAndEmptyArrays: true } },

  { $unwind: { path: "$artist.song", preserveNullAndEmptyArrays: true } },

  { $unwind: { path: "$artist.files", preserveNullAndEmptyArrays: true } },

  { $unwind: { path: "$artist.files.song", preserveNullAndEmptyArrays: true } },

  {
    $project: {
      song_name: {
        $cond: {
          if: { $ne: ["$song", null] },
          then: "$song.song_name",
          else: {
            $cond: {
              if: { $ne: ["$artist.song", null] },
              then: "$artist.song.song_name",
              else: "$artist.files.song.song_name"
            }
          }
        }
      },
      resolution: {
        $cond: {
          if: { $ne: ["$song", null] },
          then: "$song.resolution",
          else: {
            $cond: {
              if: { $ne: ["$artist.song", null] },
              then: "$artist.song.resolution",
              else: "$artist.files.song.resolution"
            }
          }
        }
      }
    }
  },

  { $match: { song_name: { $ne: null } } },

  { $sort: { resolution: 1 } },

  { $project: { song_name: 1, _id: 0 } }
]);

