      ]
    }
  },
  {
    $group: {
      _id: null,
      christopBergeId: {
        $max: {
          $cond: [
            { $and: [{ $eq: ["$staff_first_name", "Christop"] }, { $eq: ["$staff_last_name", "Berge"] }] },
            "$staff_id",
            null
          ]
        }
      },
      ashleyMedhurstId: {
        $max: {
          $cond: [
            { $and: [{ $eq: ["$staff_first_name", "Ashley"] }, { $eq: ["$staff_last_name", "Medhurst"] }] },
            "$staff_id",
            null
          ]
        }
      }
    }
  },
  {
    $lookup: {
      from: "Product",
      let: { christopBergeId: "$christopBergeId", ashleyMedhurstId: "$ashleyMedhurstId" },
      pipeline: [
        { $unwind: "$Problems" },
        {
          $match: {
            $expr: {
              $and: [
                { $eq: ["$Problems.reported_by_staff_id", "$$christopBergeId"] },
                { $eq: ["$Problems.closure_authorised_by_staff_id", "$$ashleyMedhurstId"] }
              ]
            }
          }
        },
        { $project: { product_id: 1, _id: 0 } }
      ],
      as: "Docs1"
    }
  },
  { $unwind: "$Docs1" },
  { $project: { product_id: "$Docs1.product_id", _id: 0 } }
]);

