    $project: {
      Name: 1,
      ArtistId: 1,
      AlbumCount: {
        $size: "$Album"
      }
    }
  },
  {
    $match: {
      AlbumCount: {
        $gte: 3
      }
    }
  },
  {
    $sort: {
      Name: 1
    }
  },
  {
    $project: {
      Name: 1,
      ArtistId: 1,
      _id: 0
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $unwind: {
      path: "$Docs1",
      preserveNullAndEmptyArrays: true
    }
  },
  {
    $match: {
      $or: [
        { "Docs1.PetType": { $ne: "cat" } },
        { "Docs1": { $exists: false } }
      ]
    }
  },
  {
    $group: {
      _id: "$StuID"
    }
  },
  {
    $project: {
      StuID: "$_id",
      _id: 0
    }
  }
]);

db.genre.aggregate([
  { $unwind: "$song" },
  {
    $match: {
      "song.genre_is": "blues"
    }
  },
  {
    $group: {
      _id: null,
      min_rating: { $min: "$song.rating" }
    }
  },
  {
    $project: {
      _id: 0,
      minBluesRating: 1
    }
  },
  {
    $lookup: {
      from: "genre",
      let: { min_rating: "$min_rating" },
      pipeline: [
        { $unwind: "$song" },
        {
          $match: {
            $expr: {
              $lt: ["$song.rating", "$$min_rating"]
            }
          }
        },
        {
          $project: {
            _id: 0,
            song_name: "$song.song_name"
          }
        }
      ],
      as: "Docs1"
    }
  },
  { $unwind: "$Docs1" },
  {
    $project: {
      _id: 0,
      song_name: "$Docs1.song_name"
    }
  }
]);

db.Professionals.aggregate([
