    $project: {
      professional_id: 1,
      role_code: 1,
      email_address: 1,
      count_treatment: {
        $size: "$Treatments"
      }
    }
  },
  {
    $match: {
      count_treatment: 0
    }
  },
  {
    $project: {
      professional_id: 1,
      role_code: 1,
      email_address: 1,
      _id: 0
    }
  }
]);

db.station.aggregate([
  { $unwind: "$train_station" },
