      from: "station",
      localField: "_id",
      foreignField: "Station_ID",
      as: "Docs1"
    }
  },
  { $unwind: "$Docs1" },
  { $project: {
      _id: 0,
      Name: "$Docs1.Name"
    }
  }
]);

