db.stadium.aggregate([
  {
    $group: {
      _id: null,
      avg_Capacity: { $avg: "$Capacity" }
    }
  },
  {
    $addFields: {
      avg_Capacity: "$avg_Capacity"
    }
  },
  {
    $lookup: {
      from: "stadium",
      let: { avg_Capacity: "$avg_Capacity" },
      pipeline: [
        {
          $match: {
            $expr: { $lt: ["$Capacity", "$$avg_Capacity"] }
          }
        },
        {
          $project: {
            _id: 0,
            name: 1
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $replaceRoot: { newRoot: "$Docs1" }
  }
]);

db.College.aggregate([
  {
    $match: {
      state: "FL"
    }
  },
  {
    $group: {
      _id: null,
      maxEnrInFL: { $max: "$enr" }
    }
  },
  {
    $lookup: {
      from: "College",
      pipeline: [
        {
          $match: {
            state: { $ne: "FL" }
          }
        },
        {
          $project: {
            cName: 1,
            enr: 1
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $match: {
      $expr: {
        $gt: ["$Docs1.enr", "$maxEnrInFL"]
      }
    }
  },
  {
    $project: {
      cName: "$Docs1.cName",
      _id: 0
    }
  }
]);

db.Campuses.aggregate([
