    $project: {
      _id: 0,
      CampusFee: "$csu_fees.CampusFee"
    }
  }
]);

db.city.aggregate([
  {
    $facet: {
      high_population: [
        { $match: { Population: { $gt: 1500 } } },
        { $group: { _id: "$Status" } }
      ],
      low_population: [
        { $match: { Population: { $lt: 500 } } },
        { $group: { _id: "$Status" } }
      ]
    }
  },
  {
    $project: {
      high_population: "$high_population._id",
      low_population: "$low_population._id"
    }
  },
  {
    $project: {
      shared_status: {
        $setIntersection: ["$high_population", "$low_population"]
      }
    }
  },
  {
    $unwind: "$shared_status"
  },
  {
    $project: {
      Status: "$shared_status",
      _id: 0
    }
  }
]);

