db.shop.aggregate([
  {
    $group: {
      _id: "$District",
      less_than_3000: {
        $sum: {
          $cond: [{ $lte: ["$Number_products", 3000] }, 1, 0]
        }
      },
      more_than_10000: {
        $sum: {
          $cond: [{ $gt: ["$Number_products", 10000] }, 1, 0]
        }
      }
    }
  },
  {
    $match: {
      less_than_3000: { $gt: 0 },
      more_than_10000: { $gt: 0 }
    }
  },
  {
    $project: {
      District: "$_id",
      _id: 0
    }
  }
]);

