db.Ref_Template_Types.aggregate([
  {
    $project: {
      Template_Type_Code: 1,
      hasDocuments: {
        $gt: [
          {
            $size: {
              $filter: {
                input: "$Templates.Documents",
                as: "document",
                cond: {
                  $ne: ["$$document", []]
                }
              }
            }
          },
          0
        ]
      }
    }
  },
  {
    $match: {
      hasDocuments: false
    }
  },
  {
    $project: {
      Template_Type_Code: 1,
      _id: 0
    }
  }
]);

