db.Products.aggregate([
  {
    $lookup: {
      from: "Customers",
      let: { product_id: "$product_id" },
      pipeline: [
        { $unwind: "$Customer_Orders" },
        { $unwind: "$Customer_Orders.Order_Items" },
        {
          $match: {
            $expr: { $eq: ["$Customer_Orders.Order_Items.product_id", "$$product_id"] }
          }
        },
        {
          $group: {
            _id: "$Customer_Orders.Order_Items.product_id",
            order_count: { $sum: 1 }
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $lookup: {
      from: "Suppliers",
      let: { product_id: "$product_id" },
      pipeline: [
        { $unwind: "$Product_Suppliers" },
        {
          $match: {
            $expr: { $eq: ["$Product_Suppliers.product_id", "$$product_id"] }
          }
        },
        {
          $group: {
            _id: "$Product_Suppliers.product_id",
            total_amount_purchased: { $sum: { $toDouble: "$Product_Suppliers.total_amount_purchased" } }
          }
        }
      ],
      as: "Docs2"
    }
  },
  {
    $project: {
      product_id: 1,
      order_count: { $arrayElemAt: ["$Docs1.order_count", 0] },
      total_amount_purchased: { $arrayElemAt: ["$Docs2.total_amount_purchased", 0] }
    }
  },
  {
    $match: {
      $or: [
        { order_count: { $gt: 3 } },
        { total_amount_purchased: { $gt: 80000 } }
      ]
    }
  },
  {
    $project: {
      product_id: 1,
      _id: 0
    }
  }
]);

