  {
    $project: {
      countries: {
        $filter: {
          input: "$countries",
          as: "country",
          cond: {
            $or: [
              { $gt: [{ $size: "$$country.car_makers" }, 3] },
              {
                $gt: [
                  {
                    $size: {
                      $filter: {
                        input: "$$country.car_makers",
                        as: "maker",
                        cond: {
                          $gt: [
                            {
                              $size: {
                                $filter: {
                                  input: "$$maker.model_list",
                                  as: "model",
                                  cond: {
                                    $eq: ["$$model.Model", "fiat"]
                                  }
                                }
                              }
                            },
                            0
                          ]
                        }
                      }
                    }
                  },
                  0
                ]
              }
            ]
          }
        }
      }
    }
  },
  {
    $unwind: "$countries"
  },
  {
    $project: {
   _id:0,
   CountryId: "$countries.CountryId",
      CountryName: "$countries.CountryName"
    }
  }
]);

