db.Apartment_Buildings.aggregate([
  {
    $unwind: "$Apartments"
  },
  {
    $unwind: "$Apartments.Apartment_Bookings"
  },
  {
    $project: {
      apt_number: "$Apartments.apt_number",
      booking_status_codes: "$Apartments.Apartment_Bookings.booking_status_code"
    }
  },
  {
    $group: {
      _id: "$apt_number",
      status_codes: {
        $addToSet: "$booking_status_codes"
      }
    }
  },
  {
    $match: {
      status_codes: {
        $all: ["Provisional", "Confirmed"]
      }
    }
  },
  {
    $project: {
      apt_number: "$_id",
      _id: 0
    }
  }
]);

db.country.aggregate([
  {
    $group: {
      _id: "$GovernmentForm",
      avg_LifeExpectancy: { $avg: "$LifeExpectancy" },
      sum_Population: { $sum: "$Population" }
    }
  },
  {
    $match: {
      avg_LifeExpectancy: { $gt: 72 }
    }
  },
  {
    $project: {
      _id: 0,
      GovernmentForm: "$_id",
      sum_Population: 1
    }
  }
]);

db.Student.aggregate([
  {
    $lookup: {
      from: "Pets",
      localField: "Has_Pet.PetID",
      foreignField: "PetID",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $match: {
      Sex: "F",
      "Docs1.PetType": "dog"
    }
  },
  {
    $group: {
      _id: null,
      count: {
        $sum: 1
      }
    }
  },
  {
    $project: {
      _id: 0,
      count: 1
    }
  }
]);

db.station.aggregate([

  { $unwind: "$route" },


  {
    $group: {
      _id: {
        station_id: "$id",
        network_name: "$network_name",
        services: "$services"
      },
      count: { $sum: 1 }
    }
  },


  {
    $project: {
      _id: 0,
      count: 1,
      network_name: "$_id.network_name",
      services: "$_id.services"
    }
  }
]);

db.College.aggregate([
  {
    $group: {
      _id: null,
      max_enr: { $max: "$enr" }
    }
  },
  {
    $lookup: {
      from: "College",
      let: { max_enr: "$max_enr" },
      pipeline: [
        {
          $match: {
            $expr: { $lt: ["$enr", "$$max_enr"] }
          }
        },
        {
          $project: {
            state: 1,
            _id: 0
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $replaceRoot: {
      newRoot: "$Docs1"
    }
  },
  {
    $group: {
      _id: "$state"
    }
  },
  {
    $project: {
      state: "$_id",
      _id: 0
    }
  }
]);

db.storm.aggregate([
  {
    $lookup: {
      from: "region",
      localField: "affected_region.Region_id",
      foreignField: "Region_id",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.Region_name": "Denmark"
    }
  },
  {
    $project: {
      Name: 1,
      _id: 0
    }
  }
]);

db.ACCOUNTS.aggregate([
  {
    $unwind: "$CHECKING"
  },
  {
    $group: {
      _id: null,
      maxCheckingBalance: { $max: "$CHECKING.balance" }
    }
  },
  {
    $lookup: {
      from: "ACCOUNTS",
      let: { maxCheckingBalance: "$maxCheckingBalance" },
      pipeline: [
        { $unwind: "$CHECKING" },
        {
          $match: {
            $expr: { $lt: ["$CHECKING.balance", "$$maxCheckingBalance"] }
          }
        },
        {
          $project: {
            custid: 1,
            name: 1,
            _id: 0
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $replaceRoot: { newRoot: "$Docs1" }
  }
]);

db.church.aggregate([
  {
    $lookup: {
      from: "people",
      localField: "Church_ID",
      foreignField: "wedding.Church_ID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1": { $exists: true, $not: { $size: 0 } }
    }
  },
  {
    $group: {
      _id: "$Church_ID",
      Name: { $first: "$Name" },
      WeddingCount: { $sum: { $size: "$Docs1" } }
    }
  },
  {
    $match: {
      WeddingCount: { $gte: 2 }
    }
  },
  {
    $project: {
      _id: 0,
      Name: 1
    }
  }
]);

db.Vehicles.aggregate([
  {
    $unwind: "$Lessons"
  },
  {
    $group: {
      _id: "$Lessons.vehicle_id",
      count: { $sum: 1 }
    }
  },
  {
    $sort: { count: -1 }
  },
  {
    $limit: 1
  },
  {
    $lookup: {
      from: "Vehicles",
      localField: "_id",
      foreignField: "vehicle_id",
      as: "Docs1"
    }
  },
  {
    $project: {
      _id: 0,
      vehicle_id: "$_id",
      vehicle_details: { $arrayElemAt: ["$Docs1.vehicle_details", 0] }
    }
  }
]);

db.genre.aggregate([
  {
    $unwind: "$song"
  },
  {
    $match: {
      "song.rating": { $lt: 8 }
    }
  },
  {
    $group: {
      _id: null,
      max_resolution: { $max: "$song.resolution" }
    }
  },
  {
    $lookup: {
      from: "genre",
      let: { max_resolution: "$max_resolution" },
      pipeline: [
        {
          $unwind: "$song"
        },
        {
          $match: {
            $expr: { $gt: ["$song.resolution", "$$max_resolution"] }
          }
        },
        {
          $project: {
            _id: 0,
            f_id: "$song.f_id"
          }
        }
      ],
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      _id: 0,
      f_id: "$Docs1.f_id"
    }
  }
]);

db.continents.aggregate([
  {
    $unwind: "$countries"
  },
  {
    $project: {
      CountryName: "$countries.CountryName",
      CountryId: "$countries.CountryId",
      count_car_maker: {
        $size: "$countries.car_makers"
      }
    }
  },
  {
    $match: {
      count_car_maker: {
        $gt: 0
      }
    }
  },
  {
    $project: {
      CountryName: 1,
      CountryId: 1,
      _id: 0
    }
  }
]);

db.EMPLOYEE.aggregate([
  {
    $unwind: "$DEPARTMENT"
  },
  {
    $match: {
      "DEPARTMENT.DEPT_NAME": "Biology"
    }
  },
  {
    $unwind: "$DEPARTMENT.PROFESSOR"
  },
  {
    $lookup: {
      from: "EMPLOYEE",
      localField: "DEPARTMENT.PROFESSOR.EMP_NUM",
      foreignField: "EMP_NUM",
      as: "Docs1"
    }
  },
  {
    $unwind: "$Docs1"
  },
  {
    $project: {
      EMP_FNAME: "$Docs1.EMP_FNAME",
      EMP_LNAME: "$Docs1.EMP_LNAME",
      _id: 0
    }
  }
]);

db.Papers.aggregate([
  {
    $lookup: {
      from: "Authors",
      localField: "Authorship.authID",
      foreignField: "authID",
      as: "Docs1"
    }
  },
  {
    $match: {
      "Docs1.lname": "Gibbons",
      "Docs1.fname": "Jeremy"
    }
  },
  {
    $project: {
      title: 1,
      _id: 0
    }
  }
]);
